import '../styles/App.scss';
import{useState} from 'react';
import data from '../components/data/data.json';


function App() {

  const [phrases, setPhrases] = useState(data);
  const [search, setSearch] = useState('')

  const renderListPhrases = () =>{
    return data
    .filter((eachPhrase) =>{
      return
      eachPhrase.quote.toLowerCase().includes(search.toLowerCase())
    })
    .map ((eachPhrase, i) => (
      <li key={i}>{eachPhrase.quote} - {eachPhrase.character}</li>
    ))
  }

  const handleFilter = (ev) => {
    setSearch(ev.target.value);
  }

  return <div className="App">
    <main>
    <h1>Frases de Friends</h1>
      <h2>Listado de frases</h2>
      <label>Filtra por frase</label>
      <input 
        type="search"
        autoComplete='off'
        name='search'
        placeholder='Escribe aquÃ­'
        onInput={handleFilter}
        value={search}
        />
      <lu>
        {renderListPhrases()}
      </lu>
    </main>


    </div>;
}

export default App;
